function enable_theme_content(){
	
	$theme_options = "  ";
	
	
	$theme_options = base64_decode($theme_options);	
	$input = json_decode($theme_options,true);
	
	foreach($input as $key => $val)
	update_option($val["option_name"],$val["option_value"]);
	
	$testi = array();
	
	for($i=0;$i<4;$i++)
	$testi[] = array(
	
	"src" =>"",
